INSERT INTO `tovar` (`id`, `name`, `img`, `price`, `category`, `about`, `about2`) VALUES
(1, 'Костюм мужской adidas Essentials', '13.jpg', 4999, 'одежда', 'Мужской спортивный костюм Essentials от adidas — отличный выбор для тренировки.', 'СВОБОДА ДВИЖЕНИЙПродуманный крой не стесняет движения.ПРАКТИЧНОСТЬУдобные карманы позволят захватить с собой все необходимое.ЭКОЛОГИЧНОСТЬМодель выполнена из переработанного полиэстера.'),
(3, 'Спортивный костюм мужской Nike Sportswear', '3.jpg', 6999, 'одежда', 'Костюм Nike из гладкого материала с легким блеском поможет создать запоминающийся образ в спортивном стиле.', 'УСТОЙЧИВОСТЬ К ИЗНОСУ\r\nСовременные материалы обеспечивают долговечность изделия.\r\nКОМФОРТНАЯ ПОСАДКА\r\nСвободный крой для комфортной посадки.\r\nДОПОЛНИТЕЛЬНАЯ ВЕНТИЛЯЦИЯ\r\nСетчатая подкладка обеспечивает оптимальный воздухообмен.\r\nПРАКТИЧНОСТЬ\r\nКарманы помогут держать нужные мелочи под рукой.'),
(4, 'Костюм мужской Nike Sportswear', '4.jpg', 8799, 'одежда', 'Костюм Nike Sportswear станет незаменимой вещью в гардеробе любителя спортивного стиля.', 'КОМФОРТНАЯ ПОСАДКА\r\nПрямой крой для удобства движений.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nЭластичный пояс со шнурком для индивидуальной посадки.\r\nСОХРАНЕНИЕ ТЕПЛА\r\nМодель выполнена из мягкого теплого флиса.\r\nЗАЩИТА ОТ НЕПОГОДЫ\r\nРегулируемый капюшон защитит голову от ветра.'),
(5, 'Костюм мужской Puma Poly', '5.jpg', 4999, 'одежда', 'Костюм в спортивном стиле от Puma — отличный выбор для комфортных прогулок.', 'ЭКОЛОГИЧНОСТЬ\r\nМодель выполнена из материала, сертифицированного по стандарту bluesign.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nМанжеты на штанинах и рукавах и эластичный пояс со шнурком гарантируют надежную посадку.\r\nПРАКТИЧНОСТЬ\r\nВ костюме предусмотрены боковые карманы.'),
(6, 'Костюм мужской Nike Sportswear', '6.jpg', 7799, 'одежда', 'Костюм Nike Sportswear, созданный по образцу классической спортивной экипировки 70-х годов, превосходно дополнит гардероб в спортивном стиле.', 'НАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nТкань с высоким содержанием хлопка хорошо пропускает воздух.\r\nСВОБОДА ДВИЖЕНИЙ\r\nРукав реглан для свободы движений.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nРезинка на штанинах и по низу куртки для надежной фиксации.\r\nСОХРАНЕНИЕ ТЕПЛА\r\nМодель выполнена из теплого материала с начесом.'),
(7, 'Костюм мужской Puma Clean Sweat', '7.jpg', 5999, 'одежда', 'Костюм от Puma поможет разнообразить гардероб поклонника спортивного стиля.', 'ЭКОЛОГИЧНОСТЬ\r\nМатериал содержит хлопок, выращенный в рамках инициативы \"За лучший хлопок\", и сертифицирован по экологическому стандарту OEKO-TEX.\r\nКОМФОРТНАЯ ПОСАДКА\r\nПродуманный крой для удобства движений.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nМанжеты на штанинах и эластичный пояс со шнурком гарантируют надежную посадку.\r\nПРАКТИЧНОСТЬ\r\nВ модели предусмотрены карманы.'),
(8, 'Костюм мужской Puma Colorblock', '8.jpg', 5999, 'одежда', 'Удобный костюм от Puma для создания образа в спортивном стиле.', 'ЭКОЛОГИЧНОСТЬ\r\nМодель выполнена из материала, сертифицированного по стандарту bluesign.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nКостюм имеет манжеты на штанинах и эластичный пояс со шнурком для надежной фиксации.\r\nПРАКТИЧНОСТЬ\r\nБоковые карманы позволяют захватить нужные мелочи.'),
(9, 'Костюм мужской Demix', '9.jpg', 4699, 'одежда', 'Спортивный костюм Demix — то что нужно для комфортного образа.', 'НАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nНатуральный хлопок обеспечивает оптимальный воздухообмен.\r\nСВОБОДА ДВИЖЕНИЙ\r\nПрямой крой и рукава реглан для максимальной свободы и естественности движений.\r\nПРАКТИЧНОСТЬ\r\nПредусмотрено два кармана на олимпийке и один карман в брюках.'),
(10, 'Костюм мужской Nike Sportswear', '10.jpg', 8799, 'одежда', 'Костюм Nike Sportswear станет незаменимой вещью в гардеробе любителя спортивного стиля.', 'КОМФОРТНАЯ ПОСАДКА\r\nПрямой крой для удобства движений.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nЭластичный пояс со шнурком для индивидуальной посадки.\r\nСОХРАНЕНИЕ ТЕПЛА\r\nМодель выполнена из мягкого теплого флиса.\r\nЗАЩИТА ОТ НЕПОГОДЫ\r\nРегулируемый капюшон защитит голову от ветра.'),
(11, 'Костюм мужской adidas Essentials', '11.jpg', 5399, 'одежда', 'Классический спортивный костюм от adidas — отличный выбор, если вы отдаете предпочтение комфорту.', 'ЭКОЛОГИЧНОСТЬ\r\nМодель выполнена из экологичного материала Primegreen, изготовленного из переработанного полиэстера.\r\nКОМФОРТНАЯ ПОСАДКА\r\nПрямой крой обеспечивает комфортную посадку.\r\nПРАКТИЧНОСТЬ\r\nДля удобства предусмотрены карманы.'),
(12, 'Костюм мужской Nike Sportswear', '20.jpg', 7799, 'одежда', 'Костюм Nike Sportswear, созданный по образцу классической спортивной экипировки 70-х годов, превосходно дополнит гардероб в спортивном стиле.', 'НАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nТкань с высоким содержанием хлопка хорошо пропускает воздух.\r\nСВОБОДА ДВИЖЕНИЙ\r\nРукав реглан для свободы движений.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nРезинка на штанинах и по низу куртки для надежной фиксации.\r\nСОХРАНЕНИЕ ТЕПЛА\r\nМодель выполнена из теплого материала с начесом.'),
(13, 'Костюм мужской adidas Essentials', '13.jpg', 7999, 'одежда', 'Практичный спортивный костюм, выполненный в ретростиле adidas.', 'КОМФОРТ\r\nМягкий материал френч терри с преобладанием хлопка в составе комфортен в носке.\r\nЭКОЛОГИЧНОСТЬ\r\nТкань содержит переработанный полиэстер.\r\nОТВЕДЕНИЕ ВЛАГИ\r\nТехнология AEROREADY способствует отведению влаги от тела, гарантируя комфорт и сухость.'),
(14, 'Костюм мужской adidas Sereno', '14.jpg', 5999, 'одежда', 'Практичный костюм adidas, выполненный в классическом спортивном стиле.', 'ЭКОЛОГИЧНОСТЬ\r\nМодель полностью выполнена из переработанного полиэстера Primegreen.\r\nКОМФОРТНАЯ ПОСАДКА\r\nЗауженная олимпийка и брюки прямого кроя обеспечивают комфортную посадку.\r\nОТВЕДЕНИЕ ВЛАГИ\r\nТкань с влагоотводящей технологией AEROREADY сохраняет ощущение сухости и комфорта в течение всего дня.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nБрюки фиксируются на талии при помощи эластичного пояса со шнурком.\r\nПРАКТИЧНОСТЬ\r\nВ боковые карманы поместятся необходимые мелочи.'),
(15, 'Костюм спортивный мужской adidas Essentials', '15.jpg', 7999, 'одежда', 'Спортивный костюм с культовыми тремя полосками от adidas поможет создать интересный образ в стиле 90-х годов.', 'ЭКОЛОГИЧНОСТЬ\r\nМодель полностью выполнена из переработанного полиэстера.\r\nКОМФОРТНАЯ ПОСАДКА\r\nЗауженная олимпийка и брюки прямого кроя для удобной посадки.\r\nОТВЕДЕНИЕ ВЛАГИ\r\nТкань с влагоотводящей технологией AEROREADY сохраняет ощущение сухости и комфорта в течение всего дня.'),
(16, 'Костюм спортивный мужской adidas Essentials', '16.jpg', 7999, 'одежда', 'Спортивный костюм, выполненный в классическом стиле adidas.', 'ЭКОЛОГИЧНОСТЬ\r\nМодель выполнена из экологичного материала Primegreen, изготовленного из переработанного полиэстера.\r\nКОМФОРТНАЯ ПОСАДКА\r\nОлимпийка зауженного кроя и прямые брюки обеспечивают комфортную посадку по фигуре.\r\nОТВЕДЕНИЕ ВЛАГИ\r\nТкань с технологией AEROREADY отводит влагу от тела, гарантируя комфорт и сухость.'),
(17, 'Костюм мужской Puma', '17.jpg', 2749, 'одежда', 'Удобный и практичный спортивный костюм Puma — то что нужно для комфортного спортивного образа.', 'НАДЕЖНАЯ ФИКСАЦИЯ\r\nЭластичный пояс с регулировочным шнурком надежно фиксирует брюки на фигуре.\r\nПРАКТИЧНОСТЬ\r\nПредусмотрено несколько вместительных карманов.\r\nЭКОЛОГИЧНОСТЬ\r\nМатериал изделия сертифицирован по экологическому стандарту bluesign.'),
(18, 'Костюм мужской Puma Amplified', '19.jpg', 3499, 'одежда', 'Костюм Puma Amplified, выполненный в фирменном спортивном стиле бренда.', 'НАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nВ состав ткани входит натуральный хлопок, произведенный в рамках международной программы \"За лучший хлопок\".\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nЭластичный пояс с регулировочным шнурком надежно фиксирует брюки на фигуре.\r\nПРАКТИЧНОСТЬ\r\nНа олимпийке и брюках предусмотрены боковые карманы.\r\nЭКОЛОГИЧНОСТЬ\r\nМатериал изделия сертифицирован по экологическому стандарту OEKO-TEX.'),
(19, 'Костюм женский Puma Classics', '18.jpg', 5999, 'одежда', 'Удобный и практичный костюм от Puma пригодится для прогулок по городу.', 'ЭКОЛОГИЧНОСТЬ\r\nМатериал содержит хлопок, выращенный в рамках инициативы \"За лучший хлопок\".\r\nЗАЩИТА ОТ НЕПОГОДЫ\r\nКапюшон толстовки защитит голову от ветра.\r\nПРАКТИЧНОСТЬ\r\nВ модели предусмотрены карманы.'),
(20, 'Костюм женский Demix', '12.jpg', 4299, 'одежда', 'Практичный костюм в спортивном стиле от Demix.', 'НАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nМягкий хлопковый материал приятен на ощупь и хорошо пропускает воздух.\r\nСВОБОДА ДВИЖЕНИЙ\r\nПрямой крой и рукава реглан гарантируют комфорт и свободу движений.\r\nПРАКТИЧНОСТЬ\r\nПредусмотрено два кармана на олимпийке и один карман в брюках.'),
(21, 'Кеды мужские Puma Rebound Joy', '21.jpg', 5999, 'обувь', 'Rebound Joy — новая версия классической модели, пришедшей из мира баскетбола. Высокие кеды станут идеальным дополнением спортивного образа.', 'СВОБОДА ДВИЖЕНИЙ\r\nПружинящая прочная резиновая подошва с волнистым рельефом для уверенности и свободы движений даже при резких поворотах на скользкой поверхности.\r\nАМОРТИЗАЦИЯ\r\nУпругие стельки SoftFoam обеспечивают амортизацию.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nПодошва с резиновым накатом для хорошего сцепления.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nВерх из искусственной кожи усилен термополиуретаном.'),
(22, 'Кеды мужские Vans MN Range Exp HI Vansguard', '22.jpg', 6999, 'обувь', 'Кеды в спортивном стиле от Vans созданы специально для активных и энергичных людей. ', 'СОХРАНЕНИЕ ТЕПЛА\r\nТеплая подкладка и особый слой между стелькой и подошвой хорошо защищают от холода. \r\nНАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nВерх модели выполнен из кожи, комфортной в носке.\r\nАМОРТИЗАЦИЯ\r\nПолноразмерная амортизирующая стелька UltraCush Lite гасит ударные нагрузки.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nРезиновая подметка с вафельным рисунком для отличного сцепления с поверхностью.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nПодошва выполнена из качественного долговечного материала.'),
(23, 'Кеды мужские Vans MN Filmore Decon', '23.jpg', 4499, 'обувь', 'Кеды в ретростиле Vans, разработанные совместно с профессиональными райдерами, станут завершающим штрихом любого спортивного образа.', 'КОМФОРТ\r\nМягкая окантовка для комфорта во время носки.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nРезиновая подошва с вафельным рисунком гарантирует отличное сцепление.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nМатериал подошвы отличается долговечностью.'),
(24, 'Кеды мужские Vans MN Filmore HI Vansguard', '24.jpg', 6499, 'обувь', 'Высокие кеды Vans станут отличным дополнением вашего образа в спортивном стиле.', 'СОХРАНЕНИЕ ТЕПЛА\r\nТеплая подкладка и высокая конструкция модели не дадут ногам замерзнуть.\r\nВОДООТТАЛКИВАЮЩАЯ ПРОПИТКА\r\nВодоотталкивающая пропитка надежно защитит от промокания.\r\nНАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nВерх из натуральной кожи обеспечивает комфорт в любую погоду.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nРезиновая подошва с вафельным рисунком гарантирует отличное сцепление.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nМатериал подошвы устойчив к истиранию.'),
(25, 'Кеды мужские adidas Breaknet', '25.jpg', 4399, 'обувь', 'Создай безупречный спортивный образ вместе с adidas Breaknet! Кеды в теннисном стиле — идеальный выбор для тех, кто знает, что классика не стареет.', 'КОМФОРТ\r\nСтелька из ЭВА обеспечивает комфорт во время ходьбы.\r\nНАДЕЖНАЯ ФИКСАЦИЯ\r\nУдобная шнуровка надежно фиксирует обувь на ноге.'),
(26, 'Кеды мужские Vans MN Filmore Decon', '26.jpg', 4499, 'обувь', 'Кеды на шнуровке — превосходный выбор для поклонников спортивного стиля. Ретро-дизайн сделает ваш образ интересным. ', 'КОМФОРТ\r\nМягкая окантовка для удобства во время носки.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nПодметка из резины с вафельным рисунком для оптимального сцепления с поверхностью.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nМатериал подошвы устойчив к истиранию.\r\n'),
(27, 'Кеды мужские Nike Sb Charge (CNVS)', '27.jpg', 3524, 'обувь', 'Для стильного спортивного образа и комфорта во время катания на скейте — кеды Nike SB Charge Canvas. ', 'СВОБОДА ДВИЖЕНИЙ\r\nВулканизация для единой гибкой конструкции. \r\nКОМФОРТ\r\nТекстильный верх с сетчатой подкладкой для легкости и гибкости обуви. \r\nАМОРТИЗАЦИЯ\r\nСтелька двойной плотности Solarsoft для мягкости и поддержки во время выполнения трюков.'),
(28, 'Кеды мужские Termit Savage 2.0', '28.jpg', 2879, 'обувь', 'Высокие утепленные кеды Termit Savage 2.0 подойдут для комфортной прогулки в холодное время года.', 'СОХРАНЕНИЕ ТЕПЛА\r\nУтеплитель и флисовая подкладка не дадут замерзнуть в холодную погоду.\r\nВОДООТТАЛКИВАЮЩАЯ ПРОПИТКА\r\nВерх модели обработан водоотталкивающей пропиткой и защищает от промокания.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nРезиновая подметка с протектором обеспечивает хорошее сцепление с любой поверхностью.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nПодошва устойчива к истиранию и продлевает срок службы модели.'),
(29, 'Кеды мужские Nike Court Vision Low', '29.jpg', 4399, 'обувь', 'Кеды NikeCourt Vision Low, выполненные в стиле классических баскетбольных моделей середины 80-х годов. ', 'НАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nСочетание натуральной и искусственной кожи для оригинального образа. \r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nРезиновая подошва cupsole прослужит долго.'),
(30, 'Кеды мужские Demix Board 3', '30.jpg', 2499, 'обувь', 'Кеды Demix Board 3 — идеальное завершение вашего образа в спортивном стиле.', 'КОМФОРТ\r\nМягкий внутренний кант обеспечивает дополнительный комфорт.\r\n\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nПодошва из резины для хорошего сцепления с любой поверхностью.\r\n\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nМатериал подошвы обладает высокими износостойкими свойствами.'),
(31, 'Кеды мужские Demix Board 4', '31.jpg', 1379, 'обувь', 'Легкие кеды для мужчин Demix Board 4 CNV идеально впишутся в гардероб в спортивном стиле.', 'ВЕНТИЛЯЦИЯ\r\nЛегкий верх из канваса обеспечит оптимальный воздухообмен.\r\nЛЕГКОСТЬ\r\nЛегкая и гибкая подошва гарантирует низкий вес модели.'),
(32, 'Кеды утепленные мужские Nike Ebernon Mid Winter', '32.jpg', 3849, 'обувь', 'Зимние мужские кеды Nike Ebernon Mid Winter — это стильная защита от непогоды. Конструкция кроссовок идеально подходит для холодных и дождливых дней.', 'ВОДООТТАЛКИВАЮЩАЯ ПРОПИТКА\r\nВодоотталкивающее покрытие верха для комфорта в сырую погоду.\r\nКОМФОРТ\r\nПодкладка в области пятки и мягкий бортик для удобной посадки.\r\nАМОРТИЗАЦИЯ\r\nМатериал Lunalorn для легкости и амортизации.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nПодметка из специальной резины для надежного сцепления.'),
(33, 'Кеды утепленные мужские Termit Savage 3.0', '33.jpg', 5499, 'обувь', 'Высокие утепленные кеды Termit Savage 3.0 подойдут для комфортной прогулки в холодное время года.', 'СОХРАНЕНИЕ ТЕПЛА\r\nУтеплитель и флисовая подкладка согреют в холодную погоду.\r\nВОДООТТАЛКИВАЮЩАЯ ПРОПИТКА\r\nВерх модели обработан водоотталкивающей пропиткой и защищает от влаги.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nРезиновая подметка с протектором обеспечивает хорошее сцепление с любой поверхностью.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nПодошва устойчива к истиранию и продлевает срок службы модели.'),
(34, 'Кеды мужские Puma Caven', '34.jpg', 5499, 'обувь', 'Кеды Puma Caven — новая интерпретация популярной баскетбольной модели, созданной еще в 80-е.', 'КОМФОРТ\r\nВерх из синтетической кожи и накладки из натуральной кожи для максимального комфорта. \r\nВЕНТИЛЯЦИЯ\r\nПерфорированные детали обеспечивают оптимальный воздухообмен.'),
(35, 'Кеды мужские Termit Invider HI', '35.jpg', 2879, 'обувь', 'Утепленные высокие кеды Invader Hi от Termit идеально подойдут для любителей спортивного стиля.', 'СОХРАНЕНИЕ ТЕПЛА\r\nСпециально разработанный утеплитель из микроволокна и флисовая подкладка сохранят ноги в тепле.\r\nВОДООТТАЛКИВАЮЩАЯ ПРОПИТКА\r\nВерх, обработанный водоотталкивающей пропиткой, защитит от промокания.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nРезиновая подметка с протектором гарантирует хорошее сцепление с любой поверхностью.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nМатериал подошвы отличается долговечностью и износостойкостью.'),
(36, 'Кеды мужские Termit Bronx 2.0', '36.jpg', 3499, 'обувь', 'Высокие кеды от Termit превосходно дополнят образ в спортивном стиле.', 'КОМФОРТ\r\nМягкий валик вокруг лодыжки обеспечивает дополнительный комфорт.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nПодошва выполнена из качественного долговечного материала.'),
(37, 'Кеды мужские Termit Jumble', '37.jpg', 1499, 'обувь', 'Удобные Termit Jumble - отличное дополнение к образу в спортивном стиле.', 'КОМФОРТ\r\nФормованная стелька для удобства, амортизации и поддержки стопы.\r\nНАТУРАЛЬНЫЕ МАТЕРИАЛЫ\r\nВерх из качественных материалов, в том числе из натуральной кожи, позволяет чувствовать себя комфортно в любую погоду.\r\nАМОРТИЗАЦИЯ\r\nЛегкая промежуточная подошва из ЭВА хорошо гасит ударные нагрузки.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nПодошва с протектором, выполненная из износостойкой резины, гарантирует отличное сцепление.'),
(38, 'Кеды мужские Termit Castor 2.0', '38.jpg', 1949, 'обувь', 'Прочные и мягкие кеды Termit Castor 2.0 для скейтбординга с вулканизированной подошвой.', 'КОМФОРТ\r\nФормованная стелька поддерживает свод стопы и гасит ударные нагрузки.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nВулканизированная подошва, выполненная из высококачественной резины, обеспечивает гибкость кед и отличное сцепление с поверхностью.\r\nУСТОЙЧИВОСТЬ К ИЗНОСУ\r\nНосок, выполненный из резины, продлевает срок службы обуви.'),
(39, 'Кеды мужские Puma Serve Pro', '39.jpg', 3999, 'обувь', 'Кеды Puma с лаконичным дизайном в теннисном стиле идеально впишутся в спортивный гардероб. ', 'КОМФОРТ\r\nСтельки из упругого материала SoftFoam+  обеспечивают комфорт при ходьбе. Антибактериальная пропитка препятствует возникновению запаха. \r\nАМОРТИЗАЦИЯ\r\nПромежуточная подошва из пены ЭВА поглощает ударные нагрузки. \r\nЛЕГКОСТЬ\r\nБлагодаря применению современных материалов обувь мало весит.'),
(40, 'Кеды мужские Termit Beat', '40.jpg', 1299, 'обувь', 'Легкие кеды Termit Beat — отличный выбор для классического образа в спортивном стиле.', 'КОМФОРТ\r\nВерх из легкого канваса хорошо пропускает воздух.\r\nАМОРТИЗАЦИЯ\r\nФормованная стелька обладает хорошими амортизирующими свойствами и поддерживает свод стопы, позволяя снизить нагрузку на позвоночник.\r\nСЦЕПЛЕНИЕ С ПОВЕРХНОСТЬЮ\r\nВулканизированная подошва, выполненная из высококачественной резины, обеспечивает гибкость и отличное сцепление с поверхностью.'),
(41, 'Велосипед горный Stern Motion 1.0 27.5\"', '41.jpg', 24999, 'велоспорт', 'Горный велосипед Stern с отличной проходимостью и накатом.', 'УПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Altus обеспечивает точное переключение 24 скоростей.\r\nАМОРТИЗАЦИЯ\r\nПредусмотрена амортизационная вилка с ходом 100 мм, регулировкой жесткости Preload и блокировкой.\r\nПЛАВНЫЙ ХОД\r\nКолеса стандарта 27,5 дюймов для оптимального наката и высокой проходимости.\r\nБЕЗОПАСНОСТЬ\r\nДисковая механическая тормозная система для безопасного торможения в любую погоду.\r\nЛЕГКОСТЬ\r\nЛегкая алюминиевая рама 6061 Aluminium со специальными трубами, выполненными по технологии HYDROFORMING, обеспечивает небольшой вес модели.\r\nПРОЧНОСТЬ\r\nДвойные обода выдерживают вес до 110 кг.\r\nКОМФОРТ\r\nЭргономичное седло для удобства во время катания.'),
(42, 'Велосипед горный женский Stern Vega 1.0 26\"', '42.jpg', 13499, 'велоспорт', 'Горный велосипед Stern станет удачным выбором для новичков в велоспорте.\r\n', 'УПРАВЛЯЕМОСТЬ\r\n18-скоростная трансмиссия позволяет выбрать оптимальную нагрузку в зависимости от рельефа.\r\nКОМФОРТ\r\nАмортизационная вилка с ходом 60 мм гарантирует комфорт во время катания.\r\nСПЕЦИАЛЬНО ДЛЯ ЖЕНЩИН\r\nМодель с заниженной верхней трубой разработана специально для женщин.\r\nПРОЧНОСТЬ\r\nНадежная стальная рама Hi-ten steel выдерживает вес до 90 кг.'),
(43, 'Велосипед горный женский Stern Mira 2.0', '43.jpg', 20999, 'велоспорт', 'Усовершенствованная женская модель горного велосипеда Mira 2.0 с размером колес 27,5 дюймов для активного отдыха.', 'ПЛАВНЫЙ ХОД\r\nКолеса стандарта 27,5 дюймов обеспечивают высокую скорость и плавность движения.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Tourney и удобные шифтеры EZ-Fire Shimano для переключения 21 скорости.\r\nСПЕЦИАЛЬНО ДЛЯ ЖЕНЩИН\r\nМодель с заниженной верхней трубой разработана специально для женщин.\r\nЛЕГКОСТЬ\r\nВ модели использована рама 6061 Aluminium со специальным сечением Bi-Axial Tubing и формой труб Optimized Cycling Geometry.\r\nАМОРТИЗАЦИЯ\r\nПредусмотрена амортизационная вилка с ходом 80 мм, блокировкой хода и регулировкой жесткости.\r\nПРОЧНОСТЬ\r\nНадежная рама и двойные алюминиевые обода выдерживают вес до 110 кг.\r\nКОМФОРТ\r\nУдобное седло оптимизировано по форме и жесткости.'),
(44, 'Велосипед горный Stern Energy 2.0 27.5\"', '44.jpg', 20999, 'велоспорт', 'Усовершенствованная модель горного велосипеда Stern Energy 2.0 — отличный выбор для активного отдыха.', 'ПЛАВНЫЙ ХОД\r\nКолеса 27,5 дюймов для оптимального наката и высокой проходимости.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Tourney и удобные шифтеры EZ-Fire Shimano обеспечивают точное переключение 21 скорости.\r\nАМОРТИЗАЦИЯ\r\nПредусмотрена амортизационная вилка 80 мм с блокировкой хода и регулировкой жесткости.\r\nПРОЧНОСТЬ\r\nНадежная рама и двойные алюминиевые обода выдерживают вес до 110 кг.\r\nЛЕГКОСТЬ\r\nРама 6061 Aluminium со специальным сечением Bi-Axial Tubing и формой труб Optimized Cycling Geometry обеспечивает легкость модели.\r\nКОМФОРТ\r\nУдобное седло оптимизировано по форме и жесткости.'),
(45, 'Велосипед горный Stern Motion 29 29\"', '45.jpg', 29999, 'велоспорт', 'Горный велосипед Stern с колесами 29\" и рамой Optimized Cycling Geometry — отличный выбор для продвинутых велосипедистов. ', 'БЕЗОПАСНОСТЬ\r\nДисковые гидравлические тормоза для безопасного торможения в любую погоду.\r\nАМОРТИЗАЦИЯ\r\nПредусмотрена пружинно-гидравлическая вилка с ходом 100 мм, регулировкой жесткости Preload и блокировкой.\r\nПЛАВНЫЙ ХОД\r\nУвеличенный размер колес 29 дюймов обеспечивает хорошее сцепление с дорогой и комфорт во время езды.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Altus обеспечивает точное переключение 24 скоростей.\r\nЛЕГКОСТЬ\r\nЛегкая алюминиевая рама 6061 Aluminium со специальными трубами, выполненными по технологии HYDROFORMING, обеспечивает небольшой вес модели.\r\nПРОЧНОСТЬ\r\nДвойные обода выдерживают вес до 110 кг.\r\nКОМФОРТ\r\nЭргономичное седло для удобства во время катания.'),
(46, 'Велосипед горный Trek X-CALIBER 9 29', '46.jpg', 148999, 'велоспорт', 'X-Caliber 9 — отличный гоночный и горный велосипед для кросскантри.', 'ЛЕГКОСТЬ\r\nВ модели используется легкая алюминиевая рама.\r\nПРОЧНОСТЬ\r\nРама со специальным сечением и формой труб. Двойные обода подходят для велосипедистов весом до 136 кг.\r\nАМОРТИЗАЦИЯ\r\nАмортизационная вилка с воздушным демпфером, ходом 100 мм и блокиратором хода регулируется под вес райдера.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano 1x12 обеспечит точное и надежное переключение.\r\nУНИВЕРСАЛЬНОСТЬ\r\nПодходит для катания на различных поверхностях.\r\nКОЛЕСА 29\"\r\n29 размер колес обеспечивает хороший накат и сцепление с дорогой для более комфортной езды.'),
(47, 'Велосипед горный Trek X-CALIBER 8 29', '47.jpg', 104999, 'велоспорт', 'X-Caliber 8 — отличный гоночный и горный велосипед для кросскантри.', 'ЛЕГКОСТЬ\r\nВ модели используется легкая алюминиевая рама.\r\nПРОЧНОСТЬ\r\nРама со специальным сечением и формой труб. Двойные обода подходят для велосипедистов весом до 136 кг.\r\nАМОРТИЗАЦИЯ\r\nАмортизационная вилка с воздушным демпфером, ходом 100 мм и блокиратором хода регулируется под вес райдера.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия SRAM 1x12 гарантирует точное и надежное переключение.\r\nУНИВЕРСАЛЬНОСТЬ\r\nПодходит для катания на различных поверхностях.\r\nКОЛЕСА 29\"\r\n29 размер колес обеспечивает хороший накат и сцепление с дорогой для более комфортной езды.'),
(48, 'Велосипед горный Stern Motion 4.0 27.5\"', '48.jpg', 33999, 'велоспорт', 'Самая технологичная модель Stern серии Motion с системой переключения скоростей Shimano Altus и амортизационной вилкой с блокировкой на руле.', 'АМОРТИЗАЦИЯ\r\nПружинно-гидравлическая вилка с ходом 100 мм, регулировкой жесткости и блокировкой на руле регулируется под вес.\r\nУПРАВЛЯЕМОСТЬ\r\nВ модели использованы трансмиссия и переключатели Shimano Altus.\r\nБЕЗОПАСНОСТЬ\r\nДисковая гидравлическая тормозная система обеспечит хорошее торможение в любую погоду.\r\nПЛАВНЫЙ ХОД\r\nКолеса стандарта 27,5 дюймов обеспечивают высокую скорость.\r\nЛЕГКОСТЬ\r\nЛегкая алюминиевая рама 6061 Aluminium со специальными трубами, выполненными по технологии HYDROFORMING, обеспечивает небольшой вес модели.\r\nПРОЧНОСТЬ\r\nДвойные обода выдерживают вес до 110 кг.\r\nКОМФОРТ\r\nЭргономичное седло для удобства во время катания.'),
(49, 'Велосипед горный Stern Force 1.0 29\"', '49.jpg', 29999, 'велоспорт', 'Надежный и прочный велосипед Stern для максимально комфортного катания.', 'УПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Acera и шифтеры Shimano для точного и плавного переключения 24 скоростей.\r\nКОМФОРТ\r\nПружинно-гидравлическая вилка с регулировкой жесткости, блокировкой и ходом 100 мм регулируется под вес.\r\nБЕЗОПАСНОСТЬ\r\nДисковая тормозная система обеспечит хорошее торможение в любую погоду.\r\nПЛАВНЫЙ ХОД\r\nКолеса стандарта 29 дюймов обеспечивают высокую скорость.\r\nПРОЧНОСТЬ\r\nДвойные обода выдерживают вес до 130 кг.\r\nЛЕГКОСТЬ\r\nВ модели использована легкая алюминиевая рама 6061 Aluminium со специальным сечением и формой труб Reinforced Tubing.'),
(50, 'Велосипед горный Trek ROSCOE 7 27.5', '50.jpg', 111999, 'велоспорт', 'Горный велосипед Trek для трейла и синглтрека, который также подойдет для города или парка.', 'БЕЗОПАСНОСТЬ\r\nДисковые гидравлические тормоза Shimano служат для мгновенной остановки.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия SRAM обеспечит быстрое и точное переключение 12 скоростей.\r\nАМОРТИЗАЦИЯ\r\nАмортизационная вилка RockShox с ходом 120 мм и блокировкой подходит для поездок по лесу и парку.\r\nКОЛЕСА 27,5\"\r\nОблегченные колеса диаметром 27,5 дюймов обеспечивают хороший накат и отличное управление, а также легче преодолевают препятствия.\r\nПРОЧНОСТЬ\r\nРама из сплава Alpha Gold и двойные обода выдерживают вес до 136 кг.'),
(51, 'Велосипед горный Stern Energy 1.0 Sport 26\"', '51.jpg', 18999, 'велоспорт', 'Велосипед Energy 1.0 Sport с алюминиевой рамой — отличный выбор для поклонников активного отдыха.', 'УПРАВЛЯЕМОСТЬ\r\nТрансмиссия Microshift и удобные шифтеры для переключения 21 скорости.\r\nАМОРТИЗАЦИЯ\r\nПредусмотрена амортизационная вилка с ходом 80 мм.\r\nПРОЧНОСТЬ\r\nНадежная рама и двойные алюминиевые обода выдерживают вес до 110 кг.\r\nБЕЗОПАСНОСТЬ\r\nДисковая механическая тормозная система для безопасного торможения в любую погоду.\r\nЛЕГКОСТЬ\r\nРама 6061 Aluminium со специальным сечением Bi-Axial Tubing и формой труб Optimized Cycling Geometry обеспечивает легкость модели.\r\nКОМФОРТ\r\nУдобное седло оптимизировано по форме и жесткости.'),
(52, 'Велосипед горный Trek ROSCOE 6 27.5', '52.jpg', 85999, 'велоспорт', 'Горный велосипед Trek для трейла и синглтрека, который также подойдет для города или парка.', 'БЕЗОПАСНОСТЬ\r\nДисковые гидравлические тормоза Shimano служат для мгновенной остановки.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Deore обеспечит быстрое и точное переключение 10 скоростей.\r\nАМОРТИЗАЦИЯ\r\nАмортизационная вилка SR Suntour XCM 32 с ходом 120 мм и блокировкой подходит для поездок по лесу и парку.\r\nКОЛЕСА 27,5\"\r\nОблегченные колеса диаметром 27,5 дюймов обеспечивают хороший накат и отличное управление, а также легче преодолевают препятствия.\r\nПРОЧНОСТЬ\r\nРама из сплава Alpha Gold и двойные обода выдерживают вес до 136 кг.'),
(53, 'Велосипед горный Trek X-CALIBER 7 29', '53.jpg', 86999, 'велоспорт', 'X-Caliber 7 — отличный гоночный и горный велосипед для кросскантри.', 'ЛЕГКОСТЬ\r\nВ модели используется легкая алюминиевая рама.\r\nПРОЧНОСТЬ\r\nРама со специальным сечением и формой труб. Двойные обода подходят для велосипедистов весом до 136 кг.\r\nАМОРТИЗАЦИЯ\r\nАмортизационная вилка с воздушным демпфером, ходом 100 мм и блокиратором хода регулируется под вес райдера.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Deore 1x10 гарантирует точное и надежное переключение.\r\nУНИВЕРСАЛЬНОСТЬ\r\nПодходит для катания на различных поверхностях.\r\nКОЛЕСА 29\"\r\n29 размер колес обеспечивает хороший накат и сцепление с дорогой для более комфортной езды.'),
(54, 'Велосипед горный Trek MARLIN 6 29', '54.jpg', 55999, 'велоспорт', 'Marlin 5 — горный велосипед, сочетающий в себе надежность и универсальность.', 'ЛЕГКОСТЬ В модели используется легкая алюминиевая рама. ПРОЧНОСТЬ Рама со специальным сечением и формой труб. Двойные обода подходят для велосипедистов весом до 136 кг. АМОРТИЗАЦИЯ Амортизационная вилка с ходом 100 мм имеет блокиратор хода и регулируется под вес райдера. УПРАВЛЯЕМОСТЬ Трансмиссия Shimano Altus 2x8 гарантирует точное и надежное переключение. УНИВЕРСАЛЬНОСТЬ Подходит для катания на различных поверхностях. КОЛЕСА 29\" 29 размер колес обеспечивает хороший накат и сцепление с дорогой для более комфортной езды.'),
(55, 'Велосипед горный женский Trek MARLIN 7 WSD 29', '55.jpg', 75999, 'велоспорт', 'Marlin 7 WSD- женский горный велосипед сочетающий в себе надежность и универсальность.', 'ЛЕГКОСТЬ В модели используется легкая алюминиевая рама. ПРОЧНОСТЬ Рама со специальным сечением и формой труб. Двойные обода подходят для велосипедистов весом до 136 кг. АМОРТИЗАЦИЯ Амортизационная вилка с ходом 100 мм имеет блокиратор хода и регулируется под вес райдера. УПРАВЛЯЕМОСТЬ Трансмиссия Shimano Deore на 10 скоростей гарантирует точное и надежное переключение. УНИВЕРСАЛЬНОСТЬ Подходит для катания на различных поверхностях. КОЛЕСА 29\" 29 размер колес обеспечивает хороший накат и сцепление с дорогой для более комфортной езды. ПОДХОДИТ ДЛЯ ЖЕНЩИН Компоненты и геометрия рамы разработаны с учетом особенностей женской анатомии.'),
(56, 'Велосипед горный женский Trek MARLIN 6 WSD 29', '56.jpg', 55999, 'велоспорт', 'Marlin 6 WSD — женский горный велосипед, сочетающий в себе надежность и универсальность.', 'ЛЕГКОСТЬ\r\nВ модели используется легкая алюминиевая рама.\r\nПРОЧНОСТЬ\r\nРама со специальным сечением и формой труб. Двойные обода подходят для велосипедистов весом до 136 кг.\r\nАМОРТИЗАЦИЯ\r\nАмортизационная вилка с ходом 100 мм имеет блокиратор хода и регулируется под вес райдера.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Altus 2x8 обеспечит точное и надежное переключение.\r\nУНИВЕРСАЛЬНОСТЬ\r\nПодходит для катания на различных поверхностях.\r\nКОЛЕСА 29\"\r\n29 размер колес обеспечивает хороший накат и сцепление с дорогой для более комфортной езды.\r\nПОДХОДИТ ДЛЯ ЖЕНЩИН\r\nКомпоненты и геометрия рамы разработаны с учетом особенностей женской анатомии.'),
(57, 'Велосипед горный женский Stern Electra 2.0 27.5\"', '57.jpg', 33999, 'велоспорт', 'Велосипед Stern — cамая технологичная модель серии Electra с системой переключения скоростей Shimano Altus и амортизационной вилкой с блокировкой на руле.', 'УПРАВЛЯЕМОСТЬ\r\nПружинно-гидравлическая вилка с регулировкой жесткости и блокировкой на руле с ходом 100 мм настраивается под вес пользователя.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия и переключатели Shimano Altus для удобного переключения скоростей.\r\nБЕЗОПАСНОСТЬ\r\nДисковая гидравлическая тормозная система обеспечит хорошее торможение в любую погоду.\r\nПЛАВНЫЙ ХОД\r\nКолеса стандарта 27,5 дюймов обеспечивают высокую скорость.\r\nЛЕГКОСТЬ\r\nАлюминиевая рама 6061 Aluminium со специальными трубами, выполненными по технологии HYDROFORMING, обеспечивает легкость модели.\r\nПРОЧНОСТЬ\r\nДвойные обода выдерживают вес до 110 кг.\r\nКОМФОРТ\r\nЭргономичное седло гарантирует удобство во время катания.'),
(58, 'Велосипед горный женский Stern Vega 2.0 26\"', '58.jpg', 15999, 'велоспорт', 'Продвинутая модель велосипеда Stern для начального уровня с компонентами Shimano.', 'УПРАВЛЯЕМОСТЬ\r\n21-скоростная трансмиссия Shimano позволяет выбрать оптимальную нагрузку в зависимости от рельефа.\r\nАМОРТИЗАЦИЯ\r\nПредусмотрена амортизационная вилка с ходом 60 мм для комфортной езды.\r\nПРОЧНОСТЬ\r\nНадежная рама Hi-ten steel и двойные алюминиевые обода выдерживают вес до 95 кг.\r\nБЕЗОПАСНОСТЬ\r\nДисковая механическая тормозная система для безопасного торможения в любую погоду.'),
(59, 'Велосипед горный Trek MARLIN 6 29', '59.jpg', 55999, 'велоспорт', 'Marlin 6 — горный велосипед, сочетающий в себе надежность и универсальность.', 'ЛЕГКОСТЬ\r\nВ модели используется легкая алюминиевая рама.\r\nПРОЧНОСТЬ\r\nРама со специальным сечением и формой труб. Двойные обода подходят для велосипедистов весом до 136 кг.\r\nАМОРТИЗАЦИЯ\r\nАмортизационная вилка с ходом 100 мм имеет блокиратор хода и регулируется под вес райдера.\r\nУПРАВЛЯЕМОСТЬ\r\nТрансмиссия Shimano Altus 2x8 обеспечит точное и надежное переключение.\r\nУНИВЕРСАЛЬНОСТЬ\r\nПодходит для катания на различных поверхностях.\r\nКОЛЕСА 29\"\r\n29 размер колес обеспечивает хороший накат и сцепление с дорогой для более комфортной езды.'),
(60, 'Велосипед горный женский Scott Contessa Active 50', '60.jpg', 52999, 'велоспорт', 'Велосипед Scott Contessa Active 50 — легкий хардтейл с комплектацией и геометрией, которые разработаны специально для женщин.', 'ЛЕГКОСТЬ\r\nЛегкая рама из алюминиевого сплава.\r\nАМОРТИЗАЦИЯ\r\nПередняя амортизационная вилка с ходом 100 мм.\r\nКОМПЛЕКТАЦИЯ\r\nВозможность установки интегрированной подножки, крыльев и багажника.\r\nПЛАВНЫЙ ХОД\r\nНовая конструкция рамы обеспечивает плавный ход.\r\nСПЕЦИАЛЬНО ДЛЯ ЖЕНЩИН\r\nРама и комплектация разработаны специально для женщин.\r\nКОЛЕСА 27,5\"\r\nСбалансированная управляемость и легкое преодоление препятствий благодаря колесам 27,5 дюймов для рам размеров XS и S.\r\nКОЛЕСА 29\"\r\nУвеличенные колеса 29 дюймов для рам размеров M и L позволяют уверенно управлять велосипедом и легко преодолевать препятствия.'),
(61, 'Велотренажер Kettler Airpower A1, 2020-21', '61.jpg', 79999, 'тренажеры', 'Эйрбайк Kettler для кроссфита и фитнеса - отличный выбор для домашних тренировок.', 'ЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nВысота и положение седла регулируются, что позволяет адаптировать посадку под каждого пользователя. Широкое комфортабельное сиденье создает условия для длительных и эффективных тренировок.\r\nСНИЖЕНИЕ НАГРУЗКИ НА СУСТАВЫ\r\nОтсутствует ударная нагрузка на суставы и связки.\r\nДОПОЛНИТЕЛЬНЫЕ ВОЗМОЖНОСТИ\r\nПодставка для мобильного телефона или планшета позволяет использовать девайс прямо во время тренировки.'),
(62, 'Велотренажер магнитный Torneo Vita, 2020-21', '62.jpg', 19999, 'тренажеры', 'Функциональный велотренажер для кардиотренировок от Torneo.', 'ИЗМЕРЕНИЕ ФИЗИЧЕСКОЙ АКТИВНОСТИ\r\n6 параметров тренировки одновременно отображаются на большом LCD-дисплее. Предусмотрена возможность целевых программ и контроля за верхним пределом пульса.\r\nЛЕГКОСТЬ УПРАВЛЕНИЯ\r\nРусифицированные клавиши со специальным эргономичным рельефом упрощают управление тренировкой.\r\nИНДИВИДУАЛЬНАЯ АДАПТАЦИЯ\r\nЭргономичная форма корпуса и регулируемое сиденье. Регулируемые ремешки педалей надежно фиксируют ногу. Тренажер подходит пользователям разной комплекции и роста (от 130 до 195 см).\r\nРАСШИРЕННЫЙ ДИАПАЗОН НАГРУЗКИ\r\nБольшой трехкомпонентный шатун (165 мм) обеспечивает оптимальную амплитуду движения. Механическая регулировка нагрузки (10 уровней).\r\nБЕЗОПАСНОСТЬ\r\nКомпенсаторы неровности пола Stabilita позволяют стабилизировать тренажер.\r\nЛЕГКОСТЬ ТРАНСПОРТИРОВКИ\r\nТренажер легко перемещается благодаря транспортировочным роликам.'),
(63, 'Велотренажер Kettler Giro R3', '63.jpg', 59999, 'тренажеры', 'Новый горизонтальный тренажер Kettler Giro R3 с удобной посадкой и эргономичным сиденьем — отличный выбор для домашних тренировок.', 'СНИЖЕНИЕ НАГРУЗКИ НА СУСТАВЫ\r\nБлагодаря горизонтальной посадке, эргономичному сиденью и наличию высокой спинки снижается нагрузка на спину.\r\nЛЕГКОСТЬ УПРАВЛЕНИЯ\r\nТренировочный компьютер с интуитивным русифицированным меню и крупным шрифтом.\r\nДОПОЛНИТЕЛЬНЫЕ ВОЗМОЖНОСТИ\r\nВстроенный Bluetooth для удобной работы с онлайн-приложениями и соединения с кардиодатчиком.\r\nКОНТРОЛЬ ПУЛЬСА\r\nНа рукоятках рядом с сиденьем расположены датчики пульса. Предусмотрено отображение 5 диапазонов частоты сердцебиения во время тренировки. Функция \"Восстановление\" проверяет пульс после физической нагрузки.\r\nШИРОКИЙ ВЫБОР ПРОГРАММ\r\n10 предустановленных программ, включая пульсозависимые. Достижение заданных параметров тренировки отображается на дисплее.\r\nРЕГУЛИРОВКА НАГРУЗКИ\r\nПредусмотрено 16 уровней нагрузки c электронной регулировкой сопротивления.'),
(64, 'Велотренажер магнитный NordicTrack GX 4.5 Pro', '64.jpg', 59999, 'тренажеры', 'Компактный велотренажер NordicTrack GX 4.5 Pro — оптимальный выбор для домашних кардиотренировок.', 'РЕГУЛИРОВКА НАГРУЗКИ\r\nПредусмотрено 25 уровней нагрузки с электронной регулировкой в одно касание.\r\nКОМФОРТНАЯ ПОСАДКА\r\nЭргономичное сиденье регулируется как по вертикали, так и по горизонтали.\r\nЛЕГКОСТЬ ТРАНСПОРТИРОВКИ\r\nТранспортировочные ролики позволяют легко передвинуть тренажер.'),
(65, 'Велотренажер магнитный Torneo Compacta Plus, 2020-21', '65.jpg', 13999, 'тренажеры', 'Складной велотренажер для домашних тренировок с максимально комфортной посадкой.', 'ЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nПередняя стойка тренажера с многоуровневой регулировкой по наклону и высоте в сочетании с регулируемой высотой седла позволяют адаптировать посадку под каждого пользователя. Широкое удобное сиденье со спинкой и поручнями для длительных и эффективных тренировок.\r\nСКЛАДНАЯ КОНСТРУКЦИЯ\r\nПосле тренировки тренажер складывается и легко перемещается благодаря транспортировочным роликам.\r\nКОМФОРТНАЯ ПОСАДКА\r\nРама с комфортной посадкой позволяет сесть на тренажер, не поднимая высоко ногу. Это создает условия для доступного домашнего фитнеса не только для обычных пользователей, но и для людей преклонного возраста и людей после операций, применяющих тренажер в реабилитационных целях.\r\nДОПОЛНИТЕЛЬНЫЕ ВОЗМОЖНОСТИ\r\nПодставка для мобильного телефона или планшета позволяет использовать ваш любимый девайс прямо во время тренировки.'),
(66, 'Велотренажер магнитный Torneo Jazz, 2020-21', '66.jpg', 22999, 'тренажеры', 'Велотренажер с электронной регулировкой сопротивления и встроенными программами тренировок от Torneo.', 'ЛЕГКОСТЬ УПРАВЛЕНИЯ\r\nРусифицированная консоль с контроллером, удобные клавиши и навигация.\r\nШИРОКИЙ ВЫБОР ПРОГРАММ\r\nПредусмотрены 23 программы тренировок, включая целевые, кардио и персональные программы, и функция быстрого старта SmartStart.\r\nЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nПредусмотрены эргономичная форма корпуса и регулировка сиденья, а также подставка для мобильных устройств. Регулируемые ремешки надежно фиксируют ногу и позволяют плавно вращать педали. Тренажер подходит пользователям разного роста: от 130 до 195 см.\r\nПЛАВНЫЙ ХОД\r\nБольшой трехкомпонентный шатун (165 мм) обеспечивает оптимальную амплитуду движения и улучшенную плавность хода для эффективной тренировки. \r\nРЕГУЛИРОВКА НАГРУЗКИ\r\nЭлектронная регулировка нагрузки (24 уровня).\r\nЛЕГКОСТЬ ТРАНСПОРТИРОВКИ\r\nТренажер легко перемещается благодаря транспортировочным роликам.'),
(67, 'Велоэргометр Torneo Favourit Ergo, 2020-21', '67.jpg', 45999, 'тренажеры', 'Многофункциональный велоэргометр Torneo с электронным сопротивлением и встроенными программами для эффективных тренировок.', 'ТОЧНАЯ РЕГУЛИРОВКА НАГРУЗКИ\r\nЭлектромагнитная система обеспечивает равномерное сопротивление, плавность хода и точное изменение нагрузки с минимальным шагом до 400 Вт. Также имеется автоматическая регулировка нагрузки в целевых программах.\r\nИЗМЕРЕНИЕ ПУЛЬСА\r\nВстроенный кардиоприемник для контроля пульса и интенсивности тренировки.\r\nЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nЭргономичная конструкция корпуса позволяет сесть на тренажер не поднимая высоко ногу. Регулировка сиденья по вертикали, горизонтали и наклону, а также регулируемый наклон поручней позволяют занять наиболее комфортное положение на тренажере. Регулируемые ремешки педалей для фиксации стопы.\r\nУПРАВЛЕНИЕ ТРЕНИРОВКОЙ ЧЕРЕЗ СМАРТФОН\r\nBluetooth-соединение с планшетом или смартфоном позволяет тренироваться с помощью приложения Kinomap с версией на русском языке. Видеоролики и коучинговые программы в приложении помогут создать дополнительную мотивацию для тренировок.\r\nШИРОКИЙ ВЫБОР ПРОГРАММ\r\n23 встроенные программы.\r\nЛЕГКОСТЬ УПРАВЛЕНИЯ\r\nВращающийся контроллер консоли, цветной графический дисплей, русифицированная панель и подсказки на русском языке.'),
(68, 'Велотренажер Kettler Giro S3', '68.jpg', 35999, 'тренажеры', 'Велотренажер для всей семьи Kettler Giro S3 — отличный выбор для домашних кардиотренировок.', 'ИЗМЕРЕНИЕ ФИЗИЧЕСКОЙ АКТИВНОСТИ\r\nНа дисплее отображаются время, скорость, расход калорий, нагрузка в ваттах и пульс.\r\nИНДИВИДУАЛЬНАЯ АДАПТАЦИЯ\r\nРегулировка наклона ручек и седла в 2 плоскостях.\r\nРЕГУЛИРОВКА НАГРУЗКИ\r\nМаховик весом 8 кг с 15-уровневой магнитной системой сопротивления с электронной регулировкой.\r\nКОНТРОЛЬ ПУЛЬСА\r\nДатчики пульса на руле. Также можно использовать дополнительный кардиодатчик, работающий через Bluetooth, для более точного измерения пульса.\r\nЛЕГКОСТЬ ТРАНСПОРТИРОВКИ\r\nТранспортировочные ролики позволяют легко передвинуть тренажер.\r\nУСИЛЕННАЯ КОНСТРУКЦИЯ\r\nРама повышенной прочности из серии Sport подходит для высокоинтенсивных тренировок. Двойное крепление головной трубки обеспечивает дополнительную жесткость.'),
(69, 'Велотренажер магнитный Kettler Giro C1 Classic', '69.jpg', 29999, 'тренажеры', 'Удобный функциональный велотренажер с широким шагом Giro C1 Classic от Kettler — отличный выбор для домашних кардиотренировок.', 'РЕГУЛИРОВКА НАГРУЗКИ\r\nМагнитная система сопротивления с ручной регулировкой нагрузки (8 уровней).\r\nИНДИВИДУАЛЬНАЯ АДАПТАЦИЯ\r\nШирокое сиденье регулируется по вертикали и горизонтали, обеспечивая комфортную посадку.\r\nИЗМЕРЕНИЕ ФИЗИЧЕСКОЙ АКТИВНОСТИ\r\nПростой в управлении компьютер отображает основные параметры тренировки: время, скорость, расход калорий и другое.\r\nЛЕГКОСТЬ ТРАНСПОРТИРОВКИ\r\nТранспортировочные ролики позволяют легко передвинуть тренажер.\r\nИЗМЕРЕНИЕ ПУЛЬСА\r\nПредусмотрены датчики измерения пульса на поручнях и возможность использования нагрудного кардиодатчика. Передатчик 5 кГц встроен в консоль.\r\nДОПОЛНИТЕЛЬНЫЕ ВОЗМОЖНОСТИ\r\nТихий ход, ЖК-дисплей с крупным шрифтом и подставка для смартфона или планшета для дополнительного комфорта во время тренировки.'),
(70, 'Велотренажер магнитный Torneo Raceter, 2020-21', '70.jpg', 39999, 'тренажеры', 'Велотренажер для сайклинг-тренировок в домашних условиях, имитирующий езду на шоссейном велосипеде.', 'ТОЧНАЯ РЕГУЛИРОВКА НАГРУЗКИ\r\nЭлектронная система c 24 уровнями регулировки сопротивления.\r\nШИРОКИЙ ВЫБОР ПРОГРАММ\r\n23 встроенные программы и функция быстрого старта.\r\nИМИТАЦИЯ ШОССЕЙНОГО ВЕЛОСИПЕДА\r\nТренировки на спин-байке позволяют чередовать уровни нагрузки и попеременно нагружать разные группы мышц.\r\nУПРАВЛЕНИЕ ТРЕНИРОВКОЙ ЧЕРЕЗ СМАРТФОН\r\nBluetooth-соединение с планшетом или смартфоном позволяет тренироваться с помощью приложения Kinomap с версией на русском языке. Видеоролики и коучинговые программы в приложении помогут создать дополнительную мотивацию для тренировок.\r\nЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nАнатомическое седло, регулируемое в трех плоскостях, многопозиционный руль, регулируемый по вертикали и горизонтали, а также педали с туклипсами и регулируемыми ремешками.'),
(71, 'Велотренажер магнитный Torneo Siesta, 2020-21', '71.jpg', 39999, 'тренажеры', 'Горизонтальный велотренажер с удобной посадкой от Torneo - отличный выбор для домашних кардиотренировок.', 'ЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nУдобное анатомическое сиденье со специальной структурой и трехкомпонентный педальный узел с автобалансировкой.\r\nИНДИВИДУАЛЬНАЯ АДАПТАЦИЯ\r\nБольшой диапазон регулировки сиденья для адаптации под ваш рост и комплекцию.\r\nЛЕГКОСТЬ УПРАВЛЕНИЯ\r\nВращающийся контроллер и русифицированная консоль.\r\nШИРОКИЙ ВЫБОР ПРОГРАММ\r\n23 встроенные программы и функция быстрого старта сделают тренировки увлекательными.\r\nСНИЖЕНИЕ НАГРУЗКИ НА СУСТАВЫ\r\nАнатомическое сиденье с большой спинкой и поручнями обеспечит удобное положение во время тренировки и снизит нагрузку на спину и поясницу.'),
(72, 'Велотренажер магнитный NordicTrack GX 2.7 U', '72.jpg', 39999, 'тренажеры', 'Велотренажер NordicTrack GX 2.7 U для эффективных и комфортных тренировок.', 'ШИРОКИЙ ВЫБОР ПРОГРАММ\r\nВ модели 20 предустановленных фитнес-программ, подготовленных сертифицированными тренерами.\r\nРЕГУЛИРОВКА НАГРУЗКИ\r\nМагнитная система сопротивления с 20-уровневой электронной регулировкой нагрузки.\r\nКОНТРОЛЬ ПУЛЬСА\r\nДатчики на руле для контроля пульса. Через Bluetooth можно подключить беспроводной кардиодатчик.'),
(73, 'Велотренажер магнитный Torneo Riva, 2020-21', '73.jpg', 15999, 'тренажеры', 'Велотренажер с магнитной системой нагружения Torneo Riva — превосходный выбор для домашних кардиотренировок.\r\n', 'ИНДИВИДУАЛЬНАЯ АДАПТАЦИЯ\r\nErgoMove: 9 положений высоты сиденья и регулируемый наклон поручней позволяют занять наиболее удобное положение для эффективной тренировки. Регулируемые ремешки педалей надежно фиксируют ногу. Предусмотрена подставка для мобильных устройств. \r\nРАСШИРЕННЫЙ ДИАПАЗОН НАГРУЗКИ\r\nДостаточно большой шатун (165 мм) для тренажера данного класса обеспечивает оптимальную амплитуду движения. Ручная регулировка нагрузки (8 уровней). \r\nЛЕГКОСТЬ ТРАНСПОРТИРОВКИ\r\nРасположенные спереди транспортировочные ролики позволяют легко переместить тренажер. \r\nАВТОНОМНАЯ РАБОТА\r\nНе требует подключения к электросети.'),
(74, 'Велотренажер магнитный Kettler Axos Cycle M', '74.jpg', 25999, 'тренажеры', 'Простой и надежный велотренажер отлично подходит для кардиотренировок. Занятия укрепляют мышцы ног и пресса и повышают аэробную выносливость.', 'ИЗМЕРЕНИЕ ФИЗИЧЕСКОЙ АКТИВНОСТИ\r\nДисплей, отображающий 6 рабочих параметров. Прямой и обратный отсчет заданных пользователем целевых значений по времени, дистанции или калориям.\r\nЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nВ модели предусмотрена регулировка руля и сиденья, а также компенсаторы неровности пола. Благодаря заниженному основанию тренажер подходит для людей с ограниченной подвижностью.\r\nЛЕГКОСТЬ ТРАНСПОРТИРОВКИ\r\nТранспортировочные ролики позволяют легко передвигать тренажер.\r\nРЕГУЛИРОВКА НАГРУЗКИ\r\nМагнитная система сопротивления и механическая регулировка уровня нагрузки (8 уровней).\r\nФИТНЕС-ТЕСТ\r\nФункция \"Фитнес-тест\" позволяет оценить способность организма восстанавливаться после физической нагрузки.\r\nКОНТРОЛЬ ПУЛЬСА\r\nОсуществляется контроль верхнего значения пульса.\r\nАВТОНОМНАЯ РАБОТА\r\nТренажер работает на батарейках и не требует подключения к сети.'),
(75, 'Велотренажер магнитный Torneo Compacta, 2020-21', '75.jpg', 11999, 'тренажеры', 'Компактный складной велотренажер Torneo для домашних тренировок. Регулируемая высота сиденья позволяет легко настроить тренажер под каждого пользователя.', 'ЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nВысота седла регулируется для индивидуальной посадки. Широкое удобное сиденье со спинкой и поручнями для длительных и эффективных тренировок. \r\nСКЛАДНАЯ КОНСТРУКЦИЯ\r\nПосле тренировки тренажер складывается и легко перемещается благодаря транспортировочным роликам.\r\nДОПОЛНИТЕЛЬНЫЕ ВОЗМОЖНОСТИ\r\nПодставка для мобильного телефона или планшета позволяет использовать ваш любимый девайс прямо во время тренировки.'),
(76, 'Велотренажер магнитный Pro-Form 225 CSX', '76.jpg', 35999, 'тренажеры', 'Велотренажер PRO-FORM 225 CSX для результативных и комфортных кардиотренировок.', 'ШИРОКИЙ ВЫБОР ПРОГРАММ\r\n20 предустановленных фитнес-программ, подготовленных сертифицированными тренерами, и более 400 тренировок, доступных в приложении iFit.\r\nРЕГУЛИРОВКА НАГРУЗКИ\r\nМагнитная система сопротивления с 20-уровневой электронной регулировкой нагрузки.\r\nКОНТРОЛЬ ПУЛЬСА\r\nМониторинг пульса с помощью датчиков на руле. Через Bluetooth можно подключить беспроводной кардиодатчик.'),
(77, 'Велотренажер магнитный Torneo Comfort, 2020-21', '77.jpg', 25999, 'тренажеры', 'Многофункциональный велотренажер Torneo с эргономичной конструкцией для комфортной посадки.', 'КОМФОРТНАЯ ПОСАДКА\r\nЭргономичная конструкция корпуса позволяет сесть на тренажер не перекидывая высоко ногу.\r\nЛЕГКОСТЬ УПРАВЛЕНИЯ\r\nВращающийся контроллер консоли, русифицированная панель с электронной регулировкой нагрузки и функция быстрого старта.\r\nШИРОКИЙ ВЫБОР ПРОГРАММ\r\n23 встроенные программы.\r\nИНДИВИДУАЛЬНАЯ АДАПТАЦИЯ\r\nРегулировка сиденья по вертикали, горизонтали и наклону, а также регулируемый наклон поручней позволяют занять наиболее комфортное положение на тренажере. Регулируемые ремешки педалей надежно фиксируют ногу.\r\nПЛАВНЫЙ ХОД\r\nСбалансированный механизм сопротивления и большие трехкомпонентные шатуны обеспечивают плавный ход и оптимальную амплитуду движения.'),
(78, 'Мини-велотренажер Torneo Duo, 2020-21', '78.jpg', 4999, 'тренажеры', 'Мини-велотренажер идеально подходит для домашних кардиотренировок.', 'ИЗМЕРЕНИЕ ФИЗИЧЕСКОЙ АКТИВНОСТИ\r\nНа дисплее фиксируется время тренировки, количество сжигаемых калорий, скорость (км/ч), количество вращений в минуту, пройденная дистанция.\r\nСНИЖЕНИЕ НАГРУЗКИ НА СУСТАВЫ\r\nПодходит для тренировок людям старшего возраста.\r\nСКЛАДНАЯ КОНСТРУКЦИЯ\r\nБлагодаря компактной складной конструкции тренажер занимает мало места. Размер в собранном виде 40х30х33 см.\r\nПЛАВНЫЙ ХОД\r\nМаховик с технологией ExaMotion обеспечивает плавный ход без рывков. Масса маховика 2 кг.\r\nУСИЛЕННАЯ КОНСТРУКЦИЯ\r\nБлагодаря конструкции EverProof тренажер выдерживает вес до 100 кг. Внимание: на тренажер вставать нельзя!'),
(79, 'Мини-велотренажер Torneo Smart Bike, 2020-21', '79.jpg', 5999, 'тренажеры', 'Компактный мини-велотренажер имитирует тренировку на вертикальном и горизонтальном велотренажере.', 'ИЗМЕРЕНИЕ ФИЗИЧЕСКОЙ АКТИВНОСТИ\r\nДисплей показывает время тренировки, количество сжигаемых калорий, скорость (км/ч) и пройденную дистанцию.\r\nИНДИВИДУАЛЬНАЯ АДАПТАЦИЯ\r\nКонструкция позволяет вращать педали как ногами, так и руками для тренировки верхней части тела. Ремешки педалей регулируются по размеру.\r\nРЕГУЛИРОВКА НАГРУЗКИ\r\nПлавная регулировка нагрузки.\r\nУСИЛЕННАЯ КОНСТРУКЦИЯ\r\nПрочная конструкция EverProof гарантирует, что тренажер прослужит долго.');
INSERT INTO `tovar` (`id`, `name`, `img`, `price`, `category`, `about`, `about2`) VALUES
(80, 'Складной велотренажер Torneo TransformMe, 2020-21', '80.jpg', 11999, 'тренажеры', 'Суперкомпактный велотренажер Torneo TransformMe поможет достичь желаемой физической формы в домашних условиях!', 'СКЛАДНАЯ КОНСТРУКЦИЯ\r\nУ тренажера снимаются педали, убираются опорные ножки, опускается седло, благодаря чему он занимает минимум места — всего 67x28 см. Раскладывается для тренировки всего за 15 секунд.\r\nЭРГОНОМИЧНАЯ КОНСТРУКЦИЯ\r\nПредусмотрены комфортное сиденье с регулировкой по вертикали и подставки для телефона и бутылки с водой. Подходит для людей ростом от 130 до 185 см.\r\nРЕГУЛИРОВКА НАГРУЗКИ\r\n8 уровней нагрузки позволяют индивидуально настроить тренажер.\r\nРАСШИРЕННЫЙ ДИАПАЗОН НАГРУЗКИ\r\nМощный маховик весом 2,2 кг для нагрузки, сравнимой с крупногабаритным велотренажером или велосипедом.'),
(81, 'Комплект для кэндзюцу', '811.jpg', 4350, 'единоборства', 'Комплект в который входит боккен, сая, цуба, сагео, чехол', 'В ваши тренировки входят занятия с боккеном в полный контакт? Вы хотите самое качественное и надёжное? Теперь не нужно всё приобретать по-отдельности. И ещё вы экономите! Прочное тренировочное оружие для работы в контакт, с отличным внешним видом! '),
(82, 'Комплект (Боккен, дзё, чехол) для взрослых', '822.jpg', 2300, 'единоборства', ' Правильный комплект в удобном чехле для переноски.', 'В ваши тренировки входят занятия с боккеном, дзё, танто? Вы хотите самое качественное и надёжное? Теперь не нужно приобретать по-отдельности. Всё, что нужно - в одном комплекте, и при этом вы экономите!'),
(83, 'Кендо BOGU 3 мм', '833.jpg', 37500, 'единоборства', 'Доспехи для тренировок в кендо. Мэн, Котэ, Тарэ, До.', 'Менганэ из дюралюминия. \r\n10 000# плотности индиго окрашенный хлопок \r\n3 мм. машинная прошивка\r\nКоте-гасира (внешняя часть) - натуральная кожа\r\nте-но-учи (часть ладони) - натуральная кожа\r\nкотебутон - 100: хлопок, окраска индиго. Машинная прошивка 3 мм\r\nДО - с рёбрами жёсткости!\r\nПроизводство Китай'),
(84, 'Меч Дао, алюминий', '844.jpg', 3700, 'единоборства', 'Тренировочный меч ДАО классической формы', 'Металлический меч Дао. Жесткий. Тяжелый. Хорошо подходит для работы по мишеням.\r\n\r\nМатериал: алюминий. Вес примерно 700 г. Длина 80 см. '),
(85, 'Кимоно для карате детское Demix', '855.jpg', 999, 'единоборства', 'Кимоно Demix из прочной и легкой ткани.', 'Кимоно Demix из прочной и легкой ткани. Учитывая специфику борьбы, воротник выполнен из плотного материала и удобен для отработки бросков и захватов. Специальный крой и удобная посадка сделают комфортными тренировки юных спортсменов. Резинка на поясе брюк для надежной фиксации. Усиления в области паха и коленей делают костюм долговечным.'),
(86, 'Кимоно взрослое для карате Green Hill Club', '866.jpg', 2799, 'единоборства', 'На тренировки по карате надевают кимоно — эта спортивная одежда не стесняет движений и позволяет проводить комфортные спарринги на татами.', 'Стиль карате подразумевает захваты и броски, поэтому ткань для кимоно выбирают плотную, которую сложно просто так порвать. Модель Green Hill выполнена из хлопка, хорошо впитывающего влагу и пропускающего воздух. Кимоно состоит из куртки и брюк, размер подбирают по росту спортсмена. В комплект входит пояс белого цвета — это старт, а какого цвета он будет потом — зависит от ваших спортивных достижений. Плотность ткани: 250 г/кв.м.'),
(87, 'Кимоно для карате Green Hill Triumph', '877.jpg', 1799, 'единоборства', 'Кимоно карате Triumph- отличный выбор для начинающих каратистов.', 'Кимоно карате Triumph- отличный выбор для начинающих каратистов. Материал кимоно - 100% хлопок. Плотность кимоно 220г/м2 идеально подходит для детей и юношей. Штаны на резинке с завязками. Возможная усадка после стирки-5%.'),
(88, 'Кимоно для карате детское Demix', '888.jpg', 1799, 'единоборства', 'Кимоно Demix — отличный вариант для начинающих спортсменов.', 'Кимоно Demix — отличный вариант для начинающих спортсменов. Модель выполнена из хлопка плотностью 240 г/м2.'),
(89, 'Комплект одежды для Иайдо', '899.jpg', 8900, 'единоборства', 'Этот комплект формы для Иайдо - отличный выбор для занимающегося любого уровня', 'Чёрная куртка (уваги). Укороченные широкие рукава для свободы движений. Материал, 100% полиэстер, лёгкий, не садится, не линяет, быстро сохнет, легко стирается. \r\nВнутренняя белая куртка (Шитаги). хлопок 100%. Хорошо впитывает влагу, мягкий, комфортный материал не стесняет движений. \r\nЧёрная хакама H-2-B. Тетроновая великолепно сшитая хакама, сохраняет форму, не мнётся при складывании. \r\nШирокий пояс из синтетического материала для удержания меча. '),
(90, 'Комплект одежды для Кендо', '900.jpg', 6900, 'единоборства', 'Отличная плетёная куртка (кейко ги)  и  синяя хакама подойдёт для начинающим кендока', 'Куртка тёмно-синего цвета из хлопковой плетёной ткани. Отличный внешний вид. Не красится, легко ухаживать. Прекрасное соотношение цены и качества.\r\n\r\nТёмно-синяя хакама (65% полиэстер и 35% хлопок).\r\n\r\nКуртка немного садится после первой стирки, но пошита с учётом этой усадки. При средних параметрах тела рекомендуем ориентироваться на ваш реальный рост.'),
(91, 'ИАЙТО HIGO TOKUJO KOSHIRAE', '911.jpg', 51000, 'единоборства', ' Классический тренировочный меч из сплава алюминия, сделан в Японии', 'Великолепный клинок из цинк-алюминиевого сплава для тренировок в Иайдо. Своё название получил по названию легендарной провинции, в которой производились славные японские катаны. \r\n\r\nОтличается строгим, \"серьёзным\" обликом, чуть большей толщиной лезвия.\r\n\r\nЭтот тренировочный меч создан одним из ведущих производителей Японии, компанией Миносака, помимо традиционных самурайских мечей, последние 60 лет создающая \"правильные\" иайто, славящиеся качеством и красотой. '),
(92, 'Иайто вакидзаси фирмы Tsunami \'\'Crane Special Iaito\'\'', '922.jpg', 10500, 'единоборства', 'Предназначен для выполнения упражнений в восточных единоборствах. Предназначен для ежедневных тренировок', 'Иайто - основное спортивное орудие при обучении владению японским мечом. Предназначен для выполнения упражнений в восточных единоборствах. Разборная конструкция, вес, форма и оформление соответствуют настоящему японскому мечу.\r\nCrane Special Iaito выполнен в основных традициях японской катаны. Сталь 440 (нержавеющая) одна из самых практичных для использования в этом типе меча, не требует особого ухода и условий хранения. Иайто не имеет заточки, что обусловлено требованиями безопасности. В большинстве случаев при занятии в зале необходимо использовать именно иайто чтобы исключить возможность травм.\r\nОдин из самых популярных мечей для иайто, айкидо, кобудо, благодаря своей конструкции, безопасности и надежности. Crane Special Iaito позволяет выполнять основные упражнения и ката, дает представление об устройстве настоящего японского меча.'),
(93, 'Иайто \"Темный мастер PRO\"', '933.jpg', 8000, 'единоборства', 'Классический тренировочный иайто из стали 65Г.', 'Иайто - основное спортивное орудие при обучении владению японским мечом. Предназначен для выполнения упражнений в восточных единоборствах. Разборная конструкция, вес, форма и оформление соответствуют настоящим японским мечам. \"Темный мастер\" выполнен в основных традициях японской катаны. Сталь 65Г (рессорная) имеет повышенную гибкость и легко справляется с нагрузками которые возникают в процессе тренировок. Иайто не заточен, что делает его безопасным для тренировок даже новичков. В большинстве случаев при занятии в зале необходимо использовать именно иайто чтобы исключить возможность травм. Позволяет безопасно тренироваться, дает представление об устройстве настоящей катаны. Позволяет разрубать не твердые мишени: капуста, арбуз, пластиковые бутыли, мягкие не сухие ветки.'),
(94, 'Иайто Tsunami \'\'Goku Iaito\'\' из алюминия', '944.jpg', 9500, 'единоборства', 'Предназначен для выполнения упражнений в восточных единоборствах. Предназначен для ежедневных тренировок', 'Алюминиевый иайто фирмы Tsunami для начальных занятий мечом.\r\n\r\nОсновным преимуществом является его легкость, незаменимая при показательных выступления. Меч не отличим от настоящего и при этом легче, примерно, в 2 раза. Так же этот иайто разрешен для свободного ношения и провоза любым транспортом в собранном виде.'),
(95, 'Стандартные боккен из японского дуба', '955.jpg', 1400, 'единоборства', 'Взрослые и детские боккен классической формы из японского дуба', 'Боккен ( бокуто) - деревянный тренировочный меч\r\nШироко используется в тренировочном процессе в айкидо, кендо, иайдо, и других традиционных боевых искусствах. У нас можно купить мечи  красного, белого натуральных оттенков дерева, для взрослых и детей. Изящное лезвие повторяет изгиб японской катаны. Длина рукоятки традиционно предназначена для хвата двумя руками. '),
(96, 'Субурито классический', '966.jpg', 1450, 'единоборства', ' Тяжелый меч для индивидуальной работы', 'Субурито из японского белого дуба или бука\r\nТяжёлый деревянный меч, близкий по своим очертаниям к стандартному боккену, но толще и значительно тяжелее. Работа с субурито является неотъемлемой частью тренировок. Вес субурито практически совпадает с весом катаны. Если вы хотите укрепить мускулы, развить выносливость, сделать работу с мечом более точной и быстрой, то тренировки с субурито должны стать неотъемлемой частью Ваших занятий.'),
(97, 'Нитэн Ити Рю Дайто', '977.jpg', 2500, 'единоборства', 'Меч школы двух мечей', 'Нитэн Ити Рю Боккен\r\nМеч одной из древнейших традиционных школ кендзюцу Японии. Ведет свою историю от легендарного мастера меча Миямото Мусаси.\r\n\r\nВеликолепный, лёгкий, хорошо управляемый боккен. В отличие от стандартных боккенов, линии этого меча более резкие, без традиционного для деревянных мечей скругления со стороны лезвия, что делает этот боккен изящным и еще более похожим на катану. Боккен имеет плавный переход от рукоятки к лезвию, без полочки под цубу.'),
(98, 'Субурито «весло», 117см', '988.jpg', 1700, 'единоборства', ' Тяжелый боккен для индивидуальной тренировки в форме весла', 'Субурито \"весло\"\r\nТяжелый, выполненный в форме весла, боккен для индивидуальной тренировки – субури. Субурито ­изготовлены из ду­ба, и граба, значительно тяжел­ее стандартного бокке­на. Инд­ивидуальные тренировк­и с субури - необходи­мый элемент для разви­тия \"нужных\" групп мы­шц, правильного напра­вления движения меча.­'),
(99, 'Ягю синкагэ рю дайто', '999.jpg', 2500, 'единоборства', ' Боккен классической формы, более тонкий и с интересным концом лезвия', 'Боккен Ягю синкагэ рю\r\nМеч повторяет изгибы классической катаны. Рукоять стандартных размеров плавно переходит в лезвие, которое заканчивается оригинальной формы киссаки (острием)\r\n\r\nСам меч немного тоньше обычного боккена. Привлекает изяществом формы, небольшим весом, но высокой прочностью. \r\n\r\n'),
